class vvTestType(enum.IntEnum):
    """VibrationVIEW test type enumeration"""
    TEST_SYSCHECK = 0
    TEST_SINE = 1
    TEST_RANDOM = 2
    TEST_SHOCK = 4
    TEST_TRANSIENT = 5
    TEST_REPLAY = 6
    
    @classmethod
    def get_name(cls, value):
        """Get a human-readable name for the test type"""
        try:
            return cls(value).name.replace("TEST_", "").capitalize()
        except ValueError:
            return "Unknown"